from kodi_six import xbmcaddon

LT2HTTP_HOST = "http://127.0.0.1:65225"

def init():
    global LT2HTTP_HOST

    ADDON = xbmcaddon.Addon("service.lt2http")

    try:
        LT2HTTP_HOST = "http://" + ADDON.getSetting("remote_host") + ":" + ADDON.getSetting("remote_port")
    except:
        pass


init()
